import fs from "fs";
import path from "path";
import { pki } from "node-forge";
import { wasm as wasm_tester } from "circom_tester";
import { padString, toCircomBigIntBytes } from "../../utils";
import { describe, beforeAll, it } from "vitest";

describe("JWT Checks Test", () => {
  let circuit: any;

  describe("JWT Checks", () => {
    beforeAll(async () => {
      circuit = await wasm_tester(
        path.join(__dirname, "./circuits/jwtchecks-test.circom"),
        {
          // @dev During development recompile can be set to false if you are only making changes in the tests.
          // This will save time by not recompiling the circuit every time.
          // Compile: circom "./tests/email-verifier-test.circom" --r1cs --wasm --sym --c --wat --output "./tests/compiled-test-circuit"
          recompile: true,
          output: path.join(__dirname, "./compiled-test-circuit"),
          include: path.join(__dirname, "../../node_modules"),
        }
      );
    });

    it("should JWT Checks be ok", async function () {
      const data = {
        content: padString(
          "eyJhbGciOiJSUzI1NiIsImtpZCI6IjkzNGE1ODE2NDY4Yjk1NzAzOTUzZDE0ZTlmMTVkZjVkMDlhNDAxZTQiLCJ0eXAiOiJKV1QifQ.",
          2048
        ),
        index_b64: 103 + 265,
        // index_b64: 198,
        length_b64: 42,
        payload_start_index: 103,

        ext_claim: padString(
          '"sub":"110117207114221115868",',
          256
        ), // correct
        ext_claim_length: 30,
        name_length: 5,
        colon_index: 5,
        value_index: 6,
        value_length: 23,
      }
      
      const witness = await circuit.calculateWitness(data);

      await circuit.checkConstraints(witness);
      const claim_name = '"sub"';
      const claim_value = '"110117207114221115868"';
      await circuit.assertOut(witness, {
        claim_name: padString(claim_name, claim_name.length),
        claim_value: padString(claim_value, claim_value.length)
      });
    });

    it("should JWT Checks be ok - with curly brace ending", async function () {
      const data = {
        content: padString(
          "eyJhbGciOiJSUzI1NiIsImtpZCI6IjkzNGE1ODE2NDY4Yjk1NzAzOTUzZDE0ZTlmMTVkZjVkMDlhNDAxZTQiLCJ0eXAiOiJKV1QifQ.",
          2048
        ),
        index_b64: 103 + 676,
        // index_b64: 198,
        length_b64: 23,
        payload_start_index: 103,
        ext_claim: padString(
          '"exp":1712145837}',
          256
        ), // correct
        ext_claim_length: 17,
        name_length: 5,
        colon_index: 5,
        value_index: 6,
        value_length: 10,
      }
      
      const witness = await circuit.calculateWitness(data);

      await circuit.checkConstraints(witness);
      const claim_name = '"exp"';
      const claim_value = '1712145837';
      await circuit.assertOut(witness, {
        claim_name: padString(claim_name, claim_name.length),
        claim_value: padString(claim_value, claim_value.length)
      });
    });

    it("should JWT Checks be ok - with comma prefix", async function () {
      const data = {
        content: padString(
          "eyJhbGciOiJSUzI1NiIsImtpZCI6IjkzNGE1ODE2NDY4Yjk1NzAzOTUzZDE0ZTlmMTVkZjVkMDlhNDAxZTQiLCJ0eXAiOiJKV1QifQ.",
          2048
        ),
        index_b64: 103 + 264,
        // index_b64: 198,
        length_b64: 42,
        payload_start_index: 103,

        ext_claim: padString(
          ',"sub":"110117207114221115868",',
          256
        ), // correct
        ext_claim_length: 31,
        name_length: 6,
        colon_index: 6,
        value_index: 7,
        value_length: 23,
      }
      
      const witness = await circuit.calculateWitness(data);

      await circuit.checkConstraints(witness);
      const claim_name = ',"sub"';
      const claim_value = '"110117207114221115868"';
      await circuit.assertOut(witness, {
        claim_name: padString(claim_name, claim_name.length),
        claim_value: padString(claim_value, claim_value.length)
      });
    });
  });
});
